What is the output:
class Base{
	int i ;
	Base( ){
		System.out.println("Before add in Base" + i ) ;
		add(1);
		System.out.println("After add in Base" + i ) ;
		}
	void add( int v ){
		System.out.println("In add of Base" + i ) ;
		i+= v;
		System.out.println("In add of Base" + i ) ;
		}
	void print(){
		System.out.println(i) ;
		}
	}
class Extension extends Base{
	Extension(){
		System.out.println("Before add in Extension" + i);
		add(2);
		System.out.println("After add in Extension" + i);
		}
	void add(int v){
		System.out.println("In add of extension" + i);
		i+=v*2;
		System.out.println("In add of extension" + i);
		} 
}
public class test{
	public static void main(String[ ] args){
		doit(new Extension());
		}
	static void doit(Base b){
		b.add(8);
		b.print();
		}
}

